import "core-js/modules/es.array.push.js";
import axios from "axios";
export default {
  name: "App",
  data() {
    return {
      formSubmitted: false,
      message: "Email has been successfully sent, Please check your email!",
      showSuccessMessage: false,
      errorMessage: false,
      error: "Email is already exits!",
      users: {
        uname: "",
        uemail: "",
        city: "",
        state: "",
        address: ""
      }
    };
  },
  mounted() {
    this.getprofiledata();
  },
  methods: {
    getprofiledata() {
      const isAuthenticated = sessionStorage.getItem("users");
      if (isAuthenticated == null) {
        this.$router.push("/login");
        setTimeout(() => {
          location.reload();
        }, 1000);
      }
      const getuser = sessionStorage.getItem("users");
      axios.get("http://localhost/projects/vuejs/getprofile.php", {
        params: {
          getuser
        }
      }).then(response => {
        console.log(response.data);
        this.users.uname = response.data.name;
        this.users.uemail = response.data.email;
        this.users.city = response.data.city;
        this.users.state = response.data.state;
        this.users.address = response.data.address;
      }).catch(error => {
        console.log(error);
      });
    },
    logout() {
      sessionStorage.removeItem("users");
      this.$router.push("/login");
      setTimeout(() => {
        location.reload();
      }, 1000); // 2000 milliseconds = 2 seconds
    },

    submitForms() {
      const requestOptions = {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          name: this.name,
          email: this.email,
          subject: this.subject,
          messagee: this.messagee
        })
      };
      axios.post("http://itsugestion.com/dev/vuejs/ajax.php", requestOptions).then(response => {
        console.log(response);
        this.name = "";
        this.email = "";
        this.subject = "";
        this.messagee = "";
        this.showSuccessMessage = true;
        setTimeout(() => {
          this.showSuccessMessage = false;
        }, 3500); // 2000 milliseconds = 2 seconds
      }).catch(error => {
        console.error(error);
      });
    },
